import { useEffect, useRef, useState } from "react";
import Breadcrum from "../layouts/breadcrumb";
import post from "../../store/index";
import axios from "axios";

const SignUp = () => {

    const errRef = useRef();

    const [loading, setLoading] = useState(false);
    const [success, useSuccess] = useState(false);
    const [errMsg, setErrMsg] = useState('');

    const [user, setUser] = useState({
        username: 'pelumi',
        email: 'aladesiunpelumi@gmail.com',
        password: 'Pelumi01@',
    });

    useEffect( () => {
        setErrMsg('');
    }, [user.email, user.password, user.password]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        // axios.post('https://cashiet-server.herokuapp.com/api/v1/users/signup', user)

        post.post({
            endpoint: '/users/signup',
            details: user
        })
        .then((data) => {
            console.log(data);
        })
        .catch((error) => {
            console.log(error)
        })

        console.log(user)
    };


    return (
        <>
            {/* breadcrumb start  */}
            <Breadcrum title="create account" breadcrumbItem="create account" />
            {/* breadcrumb End  */}


            {/* section start */}
            <section class="register-page section-b-space">
                <div class="container">
                    <div class="row">
                        <div class="col-lg-12">
                            <h3>create account</h3>
                            {/* <p ref={errRef}>{errRef}</p> */}
                            <div class="theme-card">
                                <form class="theme-form" onSubmit={handleSubmit}>
                                    <div class="form-row row">
                                        <div class="col-md-6">
                                            <label for="review">Username</label>
                                            <input type="text" class="form-control" 
                                                id="username" placeholder="Username"
                                                required=""
                                                value={user.username}
                                                onChange={(e) => setUser(e.target.value)}
                                            />
                                        </div>
                                        <div class="col-md-6">
                                            <label for="email">email</label>
                                            <input type="text" class="form-control" id="email"
                                                placeholder="Email" 
                                                required=""
                                                value={user.email}
                                                onChange={(e) => setUser(e.target.value)}
                                            />
                                        </div>
                                    </div>
                                    <div class="form-row row">

                                        <div class="col-md-6">
                                            <label for="review">Password</label>
                                            <input type="password" class="form-control" id="review"
                                                placeholder="Enter your password" 
                                                required=""
                                                value={user.password}
                                                onChange={(e) => setUser(e.target.value)}
                                            />
                                        </div>

                                    </div>
                                    <button type="submit" class="btn btn-solid w-auto">create Account</button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
            {/* Section ends */}
        </>
    )
}

export default SignUp;
