import { useState ,useEffect, useRef } from "react";
import Breadcrum from "../layouts/breadcrumb";
import post from "../../store/index";
// import axios from "axios";

const SignUp = () => {

    let navigate = useNavigate()
    const [user, setUser] = useState({
        username: 'pelumi',
        email: 'aladesiunpelumi@gmail.com',
        password: 'Pelumi01@',
    });
    const [Loading, setLoading] = useState(false);
    const [NotifySuccess, setNotifySuccess] = useState({ status: false, message: "" })
    const [NotifyFailed, setNotifyFailed] = useState({ status: false, message: "" })
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setAdmin((prevState) => ({
            ...prevState,
            [name]: value.replace(/\s/g, '')
        }))

    }
    const handleSubmit = async () => {
        setLoading(true)
        try {
            const response = await post.post({ endpoint: '/users/signup', details: user })
            if (response.status) {
                setLoading(false)
                setNotifySuccess({
                    ...NotifySuccess,
                    status: true,
                    message: 'Account created successfully'
                })
                navigate('/signin')
            }
            else{
                setNotifyFailed(
                    {
                        ...NotifyFailed,
                        status: true,
                        message: response.message
                    })

                setTimeout(() => {
                    setNotifyFailed(
                        {
                            ...NotifyFailed,
                            status: false,
                        })
                }, 3000);
            }
        }
        catch (e) {
            setLoading(false)
            setNotifyFailed(
                {
                    ...NotifyFailed,
                    status: true,
                    message: e.message
                })

            setTimeout(() => {
                setNotifyFailed(
                    {
                        ...NotifyFailed,
                        status: false,
                    })
            }, 3000);
        }
    }

    return (
        <>
            {/* breadcrumb start  */}
            <Breadcrum title="create account" breadcrumbItem="create account" />
            {/* breadcrumb End  */}


            {/* section start */}
            <section class="register-page section-b-space">
                <div class="container">
                    <div class="row">
                        <div class="col-lg-12">
                            <h3>create account</h3>
                            {/* <p ref={errRef}>{errRef}</p> */}
                            <div class="theme-card">
                                <form class="theme-form" onSubmit={handleSubmit}>
                                    <div class="form-row row">
                                        <div class="col-md-6">
                                            <label for="review">Username</label>
                                            <input type="text" class="form-control" 
                                                id="username" placeholder="Username"
                                                required=""
                                                onChange={handleInputChange}
                                            />
                                        </div>
                                        <div class="col-md-6">
                                            <label for="email">email</label>
                                            <input type="text" class="form-control" id="email"
                                                placeholder="Email" 
                                                required=""
                                                onChange={handleInputChange}
                                            />
                                        </div>
                                    </div>
                                    <div class="form-row row">

                                        <div class="col-md-6">
                                            <label for="review">Password</label>
                                            <input type="password" class="form-control" id="review"
                                                placeholder="Enter your password" 
                                                required=""
                                                onChange={handleInputChange}
                                            />
                                        </div>

                                    </div>
                                    <button type="submit" class="btn btn-solid w-auto">create Account</button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
            {/* Section ends */}
        </>
    )
}

export default SignUp;
