import { useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import httpService from "../../services/http-service";
import Breadcrumb from "../layouts/breadcrumb";

const VerifyEmail = () => {



    let { emailToken } = useParams()

    const confirmEmail = async (e) => {
        e.preventDefault()


        try {
            const response = await httpService.get( 'users/confirm/'+emailToken)
            console.log(response)
            if (response.status) {
                console.log(response)
            }
            else{
                throw response;
            }
        }
        catch (e) {


        }
    }

    useEffect(() => {
        confirmEmail;
    }, [])


    return (
        <>
            {/* breadcrumb start */}
            <Breadcrumb title="Verify Email" breadcrumbItem="Verify Email" />
            {/* breadcrumb End */}


            {/* section star */}
            <section className="login-page section-b-space">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-6">
                            <h3>Forgot Password</h3>
                            <div className="theme-card">
                                <form className="theme-form">
                                    <div className="form-group">
                                        <label for="email">Email</label>
                                        <input type="text" className="form-control" id="email" placeholder="Email" required />
                                    </div>
                                    <div className="form-group">
                                        <label for="review">Password</label>
                                        <input type="password" className="form-control" id="review"
                                            placeholder="Enter your password" required="" />
                                    </div><Link to={'/signin'} className="btn btn-solid">Login</Link>
                                </form>
                            </div>
                        </div>
                        <div className="col-lg-6 right-login">
                            <h3>New Customer</h3>
                            <div className="theme-card authentication-right">
                                <h6 className="title-font">Create A Account</h6>
                                <p>Sign up for a free account at our store. Registration is quick and easy. It allows you to be
                                    able to order from our shop. To start shopping click register.</p><Link to={'/signup'}
                                    className="btn btn-solid">Create an Account</Link>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
            {/* Section end */}
        </>
    )
}

export default VerifyEmail;
