import Breadcrum from "../layouts/breadcrumb";
import { UserContext } from "../../contexts/user-context";
import { useContext, useState} from "react";
import Notification from "../../components/notification";

const SignIn= () =>{

    // let navigate = useNavigate()
    const user = useContext(UserContext);
    console.log(user.userDetails);
    const [User, setUser] = useState({ email: "", password: ""});
    const [Loading, setLoading] = useState(false);
    const [NotifySuccess, setNotifySuccess] = useState({ status: false, message: "" })
    const [NotifyFailed, setNotifyFailed] = useState({ status: false, message: "" })
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUser((prevState) => ({
            ...prevState,
            [name]: value.replace(/\s/g, '')
        }))

    }

    const signin = (e) => {
        e.preventDefault();
        setLoading(true);

        user.signin({
            endpoint: "users/login",
            details: User,
        }).then((data) => {
            if (data.status) {
                setLoading(false);
                console.log(data)
                // var user_token= data.data.token;
                // localStorage.setItem('_ux', user_token);
                // setNotifySuccess({
                //     ...NotifySuccess,
                //     status:true,
                //     message:'welcome'
                // })
                // setTimeout(() => {
                //     setNotifySuccess({
                //         ...NotifySuccess,
                //         status:false,
                //     })
                // }, 3000);
                // navigate('/') 


            }
            }).catch((error) => {
                console.log(error)
            // setLoading(false);

            //     var error_message = error.response.data.message;
            //         setNotifyFailed(
            //             {
            //                 ...NotifyFailed,
            //                 status:true,
            //                 message:error_message
            //             })
            //             setTimeout(() => {
            //                 setNotifyFailed(
            //                     {
            //                         ...NotifyFailed,
            //                         status:false,
            //                     })
            //                }, 3000);

            //     setLoading(false);
            });
    };

    return (
        <>

            {/* breadcrumb start */}
            <Breadcrum title="customer's login" breadcrumbItem="Login" />
            {/* breadcrumb End */}


            {/* section star */}
            <section class="login-page section-b-space">
                <div class="container">
                    <div class="row">
                        <div class="col-lg-6">
                            <h3>Login</h3>
                            <div class="theme-card">
                                <form class="theme-form" onSubmit={signin}>
                                    <div class="form-group">
                                        <label for="email">Email</label>
                                        <input type="text" class="form-control" id="email" placeholder="Email" name="email" required="" onChange={handleInputChange} />
                                    </div>
                                    <div class="form-group">
                                        <label for="review">Password</label>
                                        <input type="password" class="form-control" id="review"
                                            placeholder="Enter your password" required="" name="password" onChange={handleInputChange}  />
                                    </div><button type="submit"  class="btn btn-solid">Login</button>
                                </form>
                            </div>
                        </div>
                        <div class="col-lg-6 right-login">
                            <h3>New Customer</h3>
                            <div class="theme-card authentication-right">
                                <h6 class="title-font">Create A Account</h6>
                                <p>Sign up for a free account at our store. Registration is quick and easy. It allows you to be
                                    able to order from our shop. To start shopping click register.</p><a href="#"
                                    class="btn btn-solid">Create an Account</a>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
            {/* Section end */}
        </>
    )
}

export default SignIn;
