import { useState } from "react";
import Breadcrumb from "../layouts/breadcrumb";
import httpService from "../../services/http-service";
import { useNavigate } from "react-router-dom";
import PasswordChecklist from "react-password-checklist"
import toast from 'react-hot-toast';
// import axios from "axios";

const SignUp = () => {
    const [password, setPassword] = useState("");

    let navigate = useNavigate();
    const [user, setUser] = useState({
        username: '',
        email: '',
        password: '',
    });
    const [Loading, setLoading] = useState(false);
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUser((prevState) => ({
            ...prevState,
            [name]: value.replace(/\s/g, ''),
            [password]: password
        }))

    }
    const handleSubmit = async (e) => {
        e.preventDefault()

        setLoading(true)
        try {
            const response = await httpService.post({ endpoint: '/users/signup', details: user })
            console.log(response)
            if (response.status) {
                setLoading(false)
                toast.success('Registration successful, check your mail to verify your email')
                navigate('/signin')
            }
            else{
                throw response;
            }
        }
        catch (e) {
            setLoading(false)
            toast.error(e.response.data.message);
        }
    }

    return (
        <>
            {/* breadcrumb start  */}
            <Breadcrumb title="create account" breadcrumbItem="create account" />
            {/* breadcrumb End  */}


            {/* section start */}
            <section class="register-page section-b-space">
                <div class="container">
                    <div class="row">
                        <div class="col-lg-12">
                            <h3>create account</h3>
                            <div class="theme-card">
                                <form class="theme-form" onSubmit={handleSubmit}>
                                    <div class="form-row row">
                                        <div class="col-md-6">
                                            <label htmlFor="review">Username</label>
                                            <input type="text" class="form-control" 
                                                id="username" placeholder="Username"
                                                required
                                                name="username"
                                                onChange={handleInputChange}
                                            />
                                        </div>
                                        <div class="col-md-6">
                                            <label htmlFor="email">email</label>
                                            <input type="text" class="form-control" id="email"
                                                placeholder="Email" 
                                                required
                                                name="email"
                                                onChange={handleInputChange}
                                            />
                                        </div>
                                    </div>
                                    <div class="form-row row">

                                        <div class="col-md-6">
                                            <label htmlFor="review">Password</label>
                                            <input type="password" class="form-control" id="review"
                                                placeholder="Enter your password" 
                                                required
                                                value={password}
                                                onChange={e => {handleInputChange; setPassword(e.target.value)} }
                                            />
                                        </div>

                                    </div>
                                    <PasswordChecklist
                                        rules={["minLength","specialChar","number","capital"]}
                                        minLength={8}
                                        value={password}
                                        messages={{
                                            minLength: "Password must be atleast 8",
                                            specialChar: "Password must have special characters",
                                            number: "Password must have a number",
                                            capital: "Password must have a capital letter",
                                        }}
                                    />
                                    <button type="submit" class="btn btn-solid w-auto mt-4">
                                        {!Loading && 'create Account'}
                                        {Loading && 'Loading..' }

                                    </button>


                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
            {/* Section ends */}
        </>
    )
}

export default SignUp;
